import { useState } from 'react';
import{Form,Row,col,Button} from 'react-bootstrap';
import { useParams } from 'react-router-dom';
import { API_GET,API_POST } from '../../api';

export default function FormUsers (){

    let params = useParams();

    const [user_id,setUserid] = useState(0);
    const [user_name,setUsername] = useState("");
    const [user_pwd,setUserpwd] = useState("");
    const [first_name,setFirstname] = useState("");
    const [last_name,setLastname] = useState("");
    const [address,setAddress] = useState("");
    const [email,setEmail] = useState(""); 
    const [role_id,setrole_id] = useState(0);

    const [validated,setValidated] = useState(false);

    const onsave = async (event)=>{
        const form = event.currentTarget;
        event.preventDefault();
        if(form.checkValidity()===false){
            event.stopPropagation();
        }else{
            if(params.user_id ==="add"){
                doCreateFormUsers();
            }else{
                doUpdateUsers();
            }
        }
    }

    const doCreateFormUsers = async() => {
        const response = await fetch(
            "http://localhost:8080/api/users/add",
            {
                method: "POST",
                headers: {
                    Accept: "application/json",
                    'Content-Type': 'application/json',
                    Authorization: "Bearer"+ localStorage.getItem("access_token")
                }
            }
        )
        console.log("เพิ่มสำเร็จ")
    }

    const doUpdateUsers = async()=>{
        console.log("อัปเดทสำเร็จ")
    }

    return(
        <>
        <Form className='container' noValidate validated={validated} onSubmit={onsave}>
            <Form.Group controlId='validateUsername'>
                <Form.Label>ชื่อผู้ใช้</Form.Label>
                <Form.Control
                    type='text'
                    value={user_name}
                    placeholder = "ชื่อผู้ใช้"
                    required
                    onChange={(e) => setUsername(e.target.value)}
                />
                <Form.Control.Feedback type="invalid">
                    กรุณากรอกชื่อผู้ใช้
                </Form.Control.Feedback>
            </Form.Group>

            <Form.Group controlId='validateUserpwd'>
                <Form.Label>รหัสผู้ใช้</Form.Label>
                <Form.Control
                    type='text'
                    value={user_pwd}
                    placeholder = "รหัสผู้ใช้"
                    required
                    onChange={(e) => setUserpwd(e.target.value)}
                />
                <Form.Control.Feedback type="invalid">
                    กรุณากรอกรหัสผู้ใช้
                </Form.Control.Feedback>
            </Form.Group>

            <Form.Group controlId='validateFirstname'>
                <Form.Label>ชื่อ</Form.Label>
                <Form.Control
                    type='text'
                    value={first_name}
                    placeholder = "ชื่อ"
                    required
                    onChange={(e) => setFirstname(e.target.value)}
                />
                <Form.Control.Feedback type="invalid">
                    กรุณากรอกชื่อ
                </Form.Control.Feedback>
            </Form.Group>

            <Form.Group controlId='validateLastname'>
                <Form.Label>นามสกุล</Form.Label>
                <Form.Control
                    type='text'
                    value={last_name}
                    placeholder = "นามสกุล"
                    required
                    onChange={(e) => setLastname(e.target.value)}
                />
                <Form.Control.Feedback type="invalid">
                    กรุณากรอกนามสกุล
                </Form.Control.Feedback>
            </Form.Group>
            <Button variant="success" as="input" type="submit" value="SAVE"/>
        </Form></>
    )
}
