import { useState } from 'react';
import{Form,Row,col,Button} from 'react-bootstrap';
import { useNavigate, useParams } from 'react-router-dom';
import { API_GET,API_POST } from '../../api';
import Adminnav from './Adminnav';

export default function FormUsers (){

    let params = useParams();
    let navigate = useNavigate();
    let pages = 1;
    
    const [user_name,setUsername] = useState("");
    const [user_pwd,setUserpwd] = useState("");
    const [role_name,setRolename] = useState("");

    const [validated,setValidated] = useState(false);

    const onsave = async (event)=>{
        const form = event.currentTarget;
        event.preventDefault();
        if(form.checkValidity()===false){
            event.stopPropagation();
        }else{
            if(params.user_id ==="add"){
                doCreateUser();
            }else{
                doUpdateUser();
            }
        }
    }

    const doCreateUser = async() => {
        
        const json = await API_POST("formUsers/add",{
            user_name : user_name,
            user_pwd : user_pwd,
            role_name : role_name
            
           
        });
        if(json.result){
            navigate("/Admin", {replace: true});
        }
    }

    const doUpdateUser = async()=>{
        console.log("อัปเดทสำเร็จ")
    }

    return(
        <>
        <Adminnav pages={pages}/>
        <Form className='container' noValidate validated={validated} onSubmit={onsave}>



            <Form.Group controlId='validateUsername'>
                <Form.Label>ชื่อผู้ใช้</Form.Label>
                <Form.Control
                    type='text'
                    value={user_name}
                    placeholder = "ชื่อผู้ใช้"
                    required
                    onChange={(e) => setUsername(e.target.value)}
                />
                <Form.Control.Feedback type="invalid">
                    กรุณากรอกชื่อผู้ใช้
                </Form.Control.Feedback>
            </Form.Group>

            

            <Form.Group controlId='validateUserpwd'>
                <Form.Label>รหัสผ่าน</Form.Label>
                <Form.Control
                    type='text'
                    value={user_pwd}
                    placeholder = "รหัสผ่าน"
                    required
                    onChange={(e) => setUserpwd(e.target.value)}
                />
                <Form.Control.Feedback type="invalid">
                    กรุณากรอกรหัสผ่าน
                </Form.Control.Feedback>
            </Form.Group>

            <Form.Group controlId='validateRolename'>
                <Form.Label>ผู้ใช้</Form.Label>
                <Form.Control
                    type='text'
                    value={role_name}
                    placeholder = "ผู้ใช้"
                    required
                    onChange={(e) => setRolename(e.target.value)}
                />
                <Form.Control.Feedback type="invalid">
                    กรุณากรอกชื่อผู้ใช้
                </Form.Control.Feedback>
            </Form.Group>
            <Button variant="success" as="input" type="submit" value="SAVE"/>
            <navigate to="Admin" replace />
        </Form></>
    )
}
