import { useEffect,useState } from "react";
import { API_GET, API_POST } from "../../api";
import ProductownerItem from "./ProductownerItem";

export default function ProductTypeowner(){


        const [productTypes, setProductType] = useState([]);
        const [productTypeId, setProductTypeId] = useState(0);
        const [products, setProducts] = useState([]);
 
        useEffect(() =>{
            async function fetcData() {
                const response = await fetch(
                    "http://localhost:8080/api/product_types",
                    {
                        method: "GET",
                        headers:{
                            Accept: "application/json",
                            'Content-Type' : 'application/json',
                            Authoriztion: "Bearer" + localStorage.getItem("access_token")
                        }
                    }
                );

                let json = await response.json();
                setProductType(json.data);
            }

            fetcData();
        }, []);


        useEffect(() =>{
            async function fetcData() {
                const response = await fetch(
                    "http://localhost:8080/api/products/type/" + productTypeId,
                    {
                        method: "GET",
                        headers:{
                            Accept: "application/json",
                            'Content-Type' : 'application/json',
                            Authoriztion: "Bearer" + localStorage.getItem("access_token")
                        }
                    }
                );

                let json = await response.json();
                setProducts(json.data);
            }

            fetcData();
        }, [productTypeId]);

    if(localStorage.getItem("access_token")){
        return(
            <div className="container">
                <select value={productTypeId} onChange={(e) => setProductTypeId(e.target.value)}>
                    <option value={0}>ประเภทสินค้า</option>
                    {
                        productTypes.map(item => (
                            <option key={item.product_type_id} value={item.product_type_id}>{item.product_type_name}</option>
                        ))
                    }

                </select>
                <div className="container mt-3">
                    {
                        products.map(item => (
                            <ProductownerItem key={item.product_id} data={item}/>
                        ))
                    }

                </div>

             </div>

        );
    }
        
    
}
