import { useEffect, useState } from "react";
import { Table } from "react-bootstrap";
import { Link } from "react-router-dom";
import { API_GET, API_POST } from "../../api";
import OrderListItem from "./OrderListItem";



export default function ProductList() {


    const [productTypes, setProductTypes] = useState([]);
    const [product_type_id, setProductTypeId] = useState(0);
    const [list, setList] = useState([]);


    useEffect(() => {
        async function fetchData() {
            const response = await fetch(
                "http://localhost:8080/api/productTypeowner",  //ดึงข้อมูลจาก productTypeowner
                {
                    method: "GET",
                    headers: {
                        Accept: "application/json",
                        'Content-Type': 'application/json',
                    }
                }
            );

            let json = await response.json();
            setProductTypes(json.data);
            console.log(json.data)
        }
        fetchData();
    }, []);


    const fetchData = async () => {
        let json = await API_GET("product");
        setProductTypes(json.data);

    }



    return (
        <>

            <div className='row mt-1'>
                <div className='row-1 table1 frame bg-ligh'>
                    <Table striped >

                        <thead className="frame2">
                            <tr>
                                <th>รหัสสินค้า</th>
                                <th>ชื่อสินค้า</th>
                                <th>ราคา</th>
                                <th>Stock</th>
                                <th>จำนวน</th>
                                <th>ราคารวม</th>
                                <th></th>
                            </tr>
                        </thead>




                        <tbody className="table-overflow text-center">
                            {
                                productTypes.map(item => (
                                    <OrderListItem
                                        key={item.product_name}
                                        data={item}
                                        list={list}
                                        setList={setList} />
                                ))
                            }
                        </tbody>





                    </Table>

                </div>



            </div> 
            <div>

                <div className="mt-5  col-8 text-center">
                    <h1>รายการสั่งซื้อสินค้า</h1>
                </div>

                <Table striped>
                    <thead className="frame1 table">
                        <tr>
                            <th>ลำดับ</th>
                            <th>ชื่อสินค้า</th>
                            <th>ราคา</th>
                            <th>จำนวน</th>
                            <th>ราคารวม</th>

                        </tr>
                    </thead>

                    <tbody>
                        {
                            list != null &&
                            list.map(item => (
                                <>
                                    <tr>
                                        <td>{item.product_id}</td>
                                        <td>{item.product_name}</td>
                                        <td>{item.price}</td>
                                        <td>{item.count}</td>
                                        <td>{item.total}</td>
                                    </tr>
                                </>
                            ))
                        }
                    </tbody>


                </Table>

            </div>
        </>
    )
}
