import { Link } from 'react-router-dom';
import "../admin/admin.css";
import Adminnav from './Adminnav';
import Users from './Users';


export default function Admin(){
    let pages = 1;

    return(
        <>
            <div className='container-fluid  '>
                <div className='row'>
                    <div className="col-lg-2 p-0 content sidebar" >
                        <Adminnav pages={pages}/>
                    </div>
                    
                    <div className="col-lg-10 p-0 title content"><br></br>
                           <Users/>
                          <select value={role_id} onChange={(e) => setRoleid(e.target.value)}>
                                    <option value={0}>ตำแหน่งผู้ใช้</option>
                                    {
                                        role_id.map(item =>(
                                            <option key={item.role_id} value={item.role_id}>{item.role_name}</option>
                                        ))
                                    }
                                </select>
                    </div>
                </div>
            </div>
        </>
    )
}
