import { useState } from 'react';
import{Form,Row,col,Button} from 'react-bootstrap';
import { useNavigate, useParams } from 'react-router-dom';
import { API_GET,API_POST } from '../../api';
import Adminnav from './Adminnav';
import "../admin/admin.css"


export default function FormUsers (){

    let params = useParams();
    let navigate = useNavigate();
    let pages = 1;
    
    const [user_name,setUsername] = useState("");
    const [user_pwd,setUserpwd] = useState("");
    const [role_name,setRolename] = useState("");

    const [validated,setValidated] = useState(false);

    const onsave = async (event)=>{
        const form = event.currentTarget;
        event.preventDefault();
        if(form.checkValidity()===false){
            event.stopPropagation();
        }else{
            if(params.user_id ==="add"){
                doCreateUser();
            }else{
                doUpdateUser();
            }
        }
    }

    const doCreateUser = async() => {
        
        const json = await API_POST("formUsers/add",{
            user_name : user_name,
            user_pwd : user_pwd,
            role_name : role_name
            
           
        });
        if(json.result){
            navigate("/Admin", {replace: true});
        }
    }

    const doUpdateUser = async()=>{
        console.log("อัปเดทสำเร็จ")
    }

    return(
        <>
            <div className='container-fluid bg-danger'>
                <div className='row'>
                    <div className="col-lg-2 content" style={{padding:"0"}}>
                        <Adminnav pages={pages}/>
                    </div>
                    <div className="col-lg-10 content" style={{background : "#FEA4B0"}}><br></br>
                            <h1><center>จัดการข้อมูลผู้ใช้</center></h1><br></br>
                    
                        <div className="col-2">
                             <Link to={"/FormUsers/add"} className="btn btn-success ms-3">{<i className="fa-solid fa-plus me-2"></i>}+ เพิ่มข้อมูล</Link>
                        </div>
                        <div className="col-10"></div>

                        <div className="container content frame">
                            <div className='table'></div>
                        <User/>

                        </div>
                    </div>
                </div>
            </div>
                            
            </>
    )
}
