import { Link } from "react-router-dom"
import { API_POST } from "../../api"
import { useEffect, useState } from "react";

export default function OrderListItem(props) {

    let [count, setCount] = useState(0);


    function incrementCount() {
        count = count + 1;
        setCount(count);
    }


    function decrementCount() {
        if (count!=0){
            count = count - 1;
             setCount(count);

        }
        
    }


    return (
        <>

                
            <tr>
              

                <td><p>{props.data.product_id}</p></td>
                <td><p>{props.data.product_name}</p></td>
                <td><p>{props.data.price}</p></td>
                <td><p>{props.data.stock}</p></td>
                <td>
                    <div className="count">
                        <button onClick={decrementCount}>-</button>

                        <div>
                            <button type="text">{count}</button>
                        </div>
                        
                        <button onClick={incrementCount}>+</button>
                        
                    </div>
                </td>

                <td></td>
                
                <td>
                    <div>
                            <Link to={"/"} className="btn btn-success ms-3">{<i className="fa-solid fa-plus me-2"></i>}+ เพิ่มสินค้า</Link>   
                    </div>
                </td>

                <td><p></p></td>
            </tr>

        </>
    )
}
