import 'bootstrap/dist/css/bootstrap.min.css';
import { useState } from 'react';
import "./Owner.css";
import Ownernav from './Ownernav';






export default function ProductTypeowner(){

    const [productTypes, setProductType] = useState([]);
    const [productTypeId, setProductTypeId] = useState(0);
    const [products, setProducts] = useState([]);

    return(
        <>
            <div className='container-fluid  '>
                <div className='row'>
                    <div className="col-lg-2 p-0 content sidebar" >
                        <Ownernav pages ={pages}/>
                    </div>
                    
                    <div className="col-lg-10 p-0 title content"><br></br>
                        <div className="">
                                <h1><center>จัดการสินค้า</center></h1><br></br>
                        </div>
                        
                        <select value={productTypeId} onChange={(e) => setProductTypeId(e.target.value)}>
                            <option value={0}>ประเภทสินค้า</option>
                            {
                                productTypes.map(item => (
                                    <option key={item.product_type_id} value={item.product_type_id}>{item.product_type_name}</option>
                                ))
                            }

                        </select>
                        <div className="container mt-3">
                            {
                                products.map(item => (
                                    <ProductownerItem key={item.product_id} data={item}/>
                                ))
                            }

                        </div>
                    </div>
                </div>
            </div>
        </>
    )
}
